export const theme = {
    colors: {
        primaryBg: '#1F1F20',
        secondaryBg: '#252527',
        accent: '#7572D5',

        font: '#FFFFFF',

        borderColor: '#4a4a4a',

        placeholderColor: '#495057',
    },

    media: {
        tablet: "screen and (max-width:768px)",
        mobile: "screen and (max-width:576px)",
        desktop: "screen and (min-width:1440px)",
    },

    animations: {
        transition: ".2s ease-in-out",
    }

}
