import styled from "styled-components";
import {theme} from "../../../styles/Theme";
import {font} from "../../../styles/Common";

const Main_Styles = styled.section`
    min-height: 100vh;
    background-color: #f1f1b3;
    display: flex;
`

const MainTitle = styled.h1`
    ${font({weight: 400, Fmax: 27, Fmin: 20})};

    p{
        display: none;
    }
`

const Name = styled.h2`
    ${font({family: "'Josefin Sans', sans-serif", weight: 700, Fmax: 50, Fmin: 36})};
    letter-spacing: 0.05em;
    margin: 10px 0;

    span {
        position: relative;
        z-index: 0;
        white-space: nowrap;

        &::before {
            content: "";
            display: inline-block;
            width: 100%;
            height: 20px;
            background-color: ${theme.colors.accent};

            position: absolute;
            bottom: 0;
            z-index: -1;
        }
    }

    @media ${theme.media.mobile}{
        margin: 15px 0 22px;
    }
`

const Photo = styled.img`
    width: 350px;
    height: 430px;
    object-fit: cover;
    margin-right: 15px;

    @media ${theme.media.mobile} {
        width: 310px;
        height: 380px;
    }
`

const SmallText=styled.span`
    font-weight: 400;
    font-size: 14px;
`

const PhotoWrapper=styled.div`
    position: relative;
    z-index: 0;

    &::before {
        content: "";
        width: 360px;
        height: 470px;
        border: 5px solid ${theme.colors.accent};

        position: absolute;
        top: -24px;
        left: 24px;
        z-index: -1;

        @media ${theme.media.mobile} {
            width: 314px;
            height: 414px;
            top: -20px;
            left: 17px;
        }
    }

    @media ${theme.media.tablet} {
        margin-top: 65px;
    }
`

const MainTextBlock=styled.div`
display: flex;
    flex-direction: column;
    align-items: start;

    @media ${theme.media.tablet}{
        align-items: center;
    }
`

export const S={
    Main: Main_Styles,
    MainTitle,
    Name,
    Photo,
    PhotoWrapper,
    SmallText,
    MainTextBlock
}
